-- Procedure structure for deleteSSO
-- ----------------------------
DROP PROCEDURE IF EXISTS `deleteSSO`;
delimiter ;;
CREATE PROCEDURE `deleteSSO`()
BEGIN
	-- 定义需要接收游标数据的变量
	DECLARE authId BIGINT(20);
	DECLARE id BIGINT(20);
	-- 结束标志	
	DECLARE isLoop BOOLEAN DEFAULT 0;	
	-- 定义（一定要使用别名）
	DECLARE cursor_authId CURSOR for SELECT t.id FROM auth t;
	DECLARE CONTINUE HANDLER for not found set isLoop=1;	-- 将结束标志绑定的游标上

	-- 打开游标
	open cursor_authId;
		-- 开始循环
		read_loop:LOOP
			-- 提取游标中的数据
			FETCH cursor_authId INTO authId;
			-- 声明何时结束循环
			IF isLoop THEN
				LEAVE read_loop;
			END IF;

			-- 循环时的事件
			set @temp_sql = concat( 
			'delete from auth_sso_', authId, 
			" where TIMESTAMPDIFF(SECOND,create_time,now())>expiry;"); 

			PREPARE temp_sql FROM @temp_sql;    
			EXECUTE temp_sql;  
			

		END LOOP;
